sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    current.test_string1 = 'output_test1223';
    // Add your code here
	var ip_test = '192.168.1.1';
    var open_ports = [2002];
    var list_flag = false;

    var gr = new GlideRecord('x_58872_needit_passlist_range');
    gr.addQuery('ip', ip_test);
    gr.addQuery('u_port_num', open_ports);
    gr.query();
    if (gr.next()) {
        // var Created = gr.getValue('sys_created_on');
        gs.info('addQuery test := ip:' +
            gr.getValue('ip') +
            '  port:' +
            gr.getValue('u_port_num') +
            ' Created:' + gr.getValue('sys_created_on'));
        // gs.info('addQuery test:' + Created );
        list_flag = true;
    }

    gr = new GlideRecord('x_58872_needit_passlist_range');
    gr.addQuery('ip', ip_test);
    gr.addEncodedQuery('u_port_numISEMPTY');
    gr.query();
    if (gr.next()) {
        gs.info('addQuery test := ip:' +
            gr.getValue('ip') +
            '  port:ISENPTY :' +
            gr.getValue('u_port_num') +
            ' Created:' + gr.getValue('sys_created_on'));
        // gs.info('addQuery test:' + Created );
        list_flag = true;
    }

    gr = new GlideRecord('x_58872_needit_passlist_range');
    gr.addQuery('ip', ip_test);
    gr.addQuery('sys_mod_count','>=','3');
    gr.addQuery('sys_mod_count','<=','5');
    gr.query();
    while (gr.next()) {
        gs.info(
			'Range - ' + 
			'addQuery test := ip:' +
            gr.getValue('ip') +
            '  port:' +
            gr.getValue('u_port_num') +
            ' Created:' + gr.getValue('sys_created_on') +
            '  sys_mod_count:' + gr.getValue('sys_mod_count') 
			);
        // gs.info('addQuery test:' + Created );
        list_flag = true;
    }

    gs.info('list_flag;' + list_flag);

})(current, previous);
